import React, { Component } from 'react'
import SearchBar from './components/SearchBar'
import Results from './components/Results'
import Header from './components/Header'

const GetReqs = {Method: 'GET', headers: { "Content-Type": "application/json" }}

class App extends Component {
  state = {
      books: [],
    }

  ConstructFetch = (e, title, printType, filter) => {
    e.preventDefault()
    this.searchBooks(this.queryCreate(title, printType, filter))
  }
  ConstructFetchParam = (title, printType, filter) => {
    const fetchArray = []
    if(title)fetchArray.push(title ? `?q=${title}` : '')
    if(printType)fetchArray.push( printType ? `printType=${printType}` : '')
    if(filter)fetchArray.push( filter ? `filter=${filter}` : '')
    return fetchArray.join('&')
  }

  searchBooks = (param) => {
    fetch(`https://www.googleapis.com/books/v1/volumes?q=${param}`,GetReqs)
      .then(res => res.json())
      .then(books => this.setState({books: books.items}))
  }

  render() {
  return (
    <div className="App">
      <Header />
      <SearchBar searchBooks={this.searchBooks}/>
      {/* <Filter /> */}
      <div className='Results'>
      <Results books={this.state.books}/>
      </div>
    </div>
  );
  }
}

export default App;
