import React, { Component } from 'react'

class SearchBar extends Component {
    executeBookSearch = (e) => {
        e.preventDefault()
        this.props.searchBooks(e.target.searchInput.value)
    }
    render() {
        return (
        <div>
                <form className="search-form" onSubmit={this.executeBookSearch}>
                    <label htmlFor="searchInput">Search:</label>
                    <input 
                        type="text" 
                        placeholder="Enter search term"
                        name="searchInput"
                    />
                    <button type="submit">Find Books</button>
                </form>
          </div>
        )
    }
}

export default SearchBar
