import React, { Component } from 'react'
import SearchBar from './components/SearchBar'
import Filter from './components/Filter'
import Results from './components/Results'
import Header from './components/Header'

class App extends Component {
  state = {
      books: [],
    }

  ConstructFetch = (e, title, printType, filter) => {
    e.preventDefault()
    this.searchBooks(this.queryCreate(title, printType, filter))
  }
  queryCreate = (title, printType, filter) => {
    const queryArray = []
    if(title) queryArray.push(title ? `?q=${title}` : '')
    if(printType)queryArray.push( printType ? `printType=${printType}` : '')
    if(filter)queryArray.push( filter ? `filter=${filter}` : '')
    return queryArray.join('&')
  }

  searchBooks = (param) => {
    fetch(`https://www.googleapis.com/books/v1/volumes?q=${param}`)
      .then(res => res.json())
      .then(books => this.setState({books: books.items}))
  }

  render() {
  return (
    <div className="App">
      <Header />
      <SearchBar searchBooks={this.searchBooks}/>
      {/* <Filter /> */}
      <div className='Results'>
      <Results books={this.state.books}/>
      </div>
    </div>
  );
  }
}

export default App;
